test1 = 'mjqjpqmgbljsphdztnvjfqwrcgsmlb' #7
test2 = 'bvwbjplbgvbhsrlpgdmjqwftvncz' #5
prod = ''


def marker(input)
  start = 0
  stop = 14
  codeFound = false

  while !codeFound
    array = []
    array = input[start...stop].split("")
    if array.uniq.length == 14
      codeFound = true
    end
    start += 1
    stop += 1
  end

  return stop -= 1
end


puts marker(test1)
puts marker(test2)
puts marker(prod)